import React from 'react';
import { Link } from 'react-router-dom';

const Task = ({ task }) => (
    <div className="task">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
        <p>Status: {task.status}</p>
        <Link to={"/edit/"+task._id}>edit</Link>
    </div>
);

export default Task;
