import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditTask = (props) => {
    const [task, setTask] = useState({
        title: '',
        description: '',
        status: '',
    });

    useEffect(() => {
        axios.get('http://localhost:5000/tasks/'+props.match.params.id)
            .then(response => {
                setTask({
                    title: response.data.title,
                    description: response.data.description,
                    status: response.data.status,
                });
            })
            .catch(function (error) {
                console.log(error);
            })
    }, [props.match.params.id]);

    const onChangeTask = (e) => {
        e.persist();
        setTask((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    const onSubmit = (e) => {
        e.preventDefault();
        const updatedTask = {
            title: task.title,
            description: task.description,
            status: task.status,
        };
        axios.post('http://localhost:5000/tasks/update/'+props.match.params.id, updatedTask)
            .then(res => console.log(res.data));
        window.location = '/';
    };

    return (
        <div>
            <h3>Edit Task</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label>Title: </label>
                    <input  type="text" required className="form-control" name="title" value={task.title} onChange={onChangeTask} />
                </div>
                <div className="form-group">
                    <label>Description: </label>
                    <textarea  type="text" required className="form-control" name="description" value={task.description} onChange={onChangeTask} />
                </div>
                <div className="form-group">
                    <label>Status: </label>
                    <select name="status" value={task.status} onChange={onChangeTask}>
                        <option value="Pending">Pending</option>
                        <option value="In Progress">In Progress</option>
                        <option value="Done">Done</option>
                    </select>
                </div>
                <div className="form-group">
                    <input type="submit" value="Edit Task" className="btn btn-primary" />
                </div>
            </form>
        </div>
    )
}

export default EditTask;
