import axios from 'axios';
import { Link } from "react-router-dom";

const Task = props => {
  // DELETE Task
const deleteTask = async () => {
  try {
    await axios.delete('http://localhost:5000/tasks/' + props.task.id); // It will wait until delete request is finished
    window.location = '/'; // This should trigger a re-render of your component
  } catch (error) {
    console.error(error);
  }
};


  // Rest of the component...
  return (
    <div>
      <h3>{props.task.title}</h3>
      <p>{props.task.description}</p>
      <p>Status: {props.task.status}</p>
      <Link to={"/edit/" + props.task.id}>Edit</Link>
      <button onClick={deleteTask}>Delete</button>
    </div>
  );
};

export default Task;
